template.value:
SET @search_value = $search$;
SET @search_value = concat('%', @search_value,'%');
SELECT @search_value; SELECT TABLE_SCHEMA, TABLE_NAME, COLUMN_NAME, DATA_TYPE FROM information_schema.COLUMNS WHERE (COLUMN_NAME LIKE @search_value OR TABLE_NAME LIKE @search_value OR TABLE_SCHEMA LIKE @search_value) ORDER BY TABLE_SCHEMA, TABLE_NAME, COLUMN_NAME
